import React from "react";
import classes from "./Cart.module.css";
import Modal from "../UI/Modal";

const Cart = () => {
  const cartItems = (
    <ul className={classes["cart-items"]}>
      {[
        {
          id: "m1",
          name: "Malai kofta",
          amount: "1",
          price: 16.99,
        },
        {
          id: "m2",
          name: "Palak Paneer",
          amount: "2",
          price: 17.99,
        },
      ].map((meal) => (
        <li>
          id={meal.id} name={meal.name} amount={meal.amount}
        </li>
      ))}
    </ul>
  );

  return (
    <Modal> 
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount:-</span>
        <span>$31.50</span>
      </div>
      <div>
        <button>Close</button>
        <button>Order</button>
      </div>
    </Modal>
  );
};

export default Cart;
