import React, { useReducer } from "react";
import CardContext from "./cart-context";

const defaultState = { item: [], totalAmount: 0 };

const cartReducer = (state, action) => {
  switch (action.type) {
    case "ADD":

      const updatedTotalAmount =
      state.totalAmount + action.item.price * action.item.amount;

      const existingCartItemIndex=state.item.findIndex( item => item.id===state.item.id);
      const existingCartItem=state.item[existingCartItemIndex];

      let updatedItems;

      if(existingCartItem){
        const updatedItem={
          ...existingCartItem, 
          amount:existingCartItem.amount+action.item.amount
        };
        updatedItems=[...state.item];
        updatedItems[existingCartItem]=updatedItem;
      }
      else{
        updatedItems = state.item.concat(action.item);
      }

      return { item: updatedItems, totalAmount: updatedTotalAmount };
    case "REMOVE":
      const removedItems = state.item.remove();
      return { removedItems };
    default:
      return defaultState;
  }
};

const CartProvider = (props) => {
  const [cartState, dispatchCartState] = useReducer(cartReducer, defaultState);

  const addItemToCartHandler = (item) => {
    dispatchCartState({ type: "ADD", item: item });
  };
  const removeItemToCartHandler = (id) => {
    dispatchCartState({ type: "REMOVE", id: id });
  };

  const cartContext = {
    item: cartState.item,
    totalAmount: cartState.totalAmount,
    addItem: addItemToCartHandler,
    removeItem: removeItemToCartHandler,
  };
  return (
    <CardContext.Provider value={cartContext}>
      {props.children}
    </CardContext.Provider>
  );
};

export default CartProvider;
